import java.util.Scanner;

public class B10950 {
	// ¹®Á¦ : A+B - 3
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		for (int i = 0; i < n; i++) {
			int x = sc.nextInt();
			int y = sc.nextInt();
			System.out.println(x + y);
		}

	}
}
