import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class B17478 {
	// 문제 : 재귀함수가 뭔가요?
	static BufferedReader br = new BufferedReader(new InputStreamReader(System.in)); // 선언
	static StringBuilder sb = new StringBuilder(); // 할당된 버퍼에 값 넣어주기

	public static void main(String[] args) throws IOException {
		// TODO Auto-generated method stub

		int inputs = Integer.parseInt(br.readLine());
		sb.append("어느 한 컴퓨터공학과 학생이 유명한 교수님을 찾아가 물었다.\n");
		recursion(inputs, "");
		System.out.println(sb);
	}

	public static void recursion(int index, String t) {
		sb.append(t + "\"재귀함수가 뭔가요?\"\n");
		if (index == 0) {
			sb.append(t + "\"재귀함수는 자기 자신을 호출하는 함수라네\"\n");
			sb.append(t + "라고 답변하였지.\n");
			return;
		}
		sb.append(t + "\"잘 들어보게. 옛날옛날 한 산 꼭대기에 이세상 모든 지식을 통달한 선인이 있었어.\n");
		sb.append(t + "마을 사람들은 모두 그 선인에게 수많은 질문을 했고, 모두 지혜롭게 대답해 주었지.\n");
		sb.append(t + "그의 답은 대부분 옳았다고 하네. 그런데 어느 날, 그 선인에게 한 선비가 찾아와서 물었어.\"\n");
		if (index != 0)
			recursion(index - 1, t + "____");
		sb.append(t + "라고 답변하였지.\n");
	}
}
